		var oTimelineItem = {  
				dateTime: oFeedEntry.CreatedAt,
				userName: oFeedEntry.Creator.FullName,
				userEmail: oFeedEntry.Creator.Email,
				title: oFeedEntry.Action,
				text: oFeedEntry.Text,
				icon: POST_ICON
		};
