            var getSelectedTabStrip = function(tabBar) {
                var selectedKey = tabBar.getSelectedKey();
                var items = tabBar.getItems();
                for (var i = 0; i < items.length; ++i) {
                    var item = items[i];
                    var key = item.getKey() || item.getId();
                    if (key === selectedKey) {
                        return item.getBindingContext().getObject();
                    }
                }
                return null;
            };

