                upperCase: true,
                expandable: false,
                selectedKey: {
                    path: '/tabStrips/selected/objectName/value',
                },
                select: function() {
                    var tabStrip = getSelectedTabStrip(tabBar);
                    self.getModel().setDataSource(tabStrip);
                }
